const cells=document.querySelectorAll(".card"),scoreList=document.querySelectorAll(".scoreList"),statusText=document.querySelector("#statusText"),winnerText=document.querySelector("#winnerText"),restartBtn=document.querySelector("#restartBtn");let winner;const winConditions=[[0,1,2],[3,4,5],[6,7,8],[0,3,6],[1,4,7],[2,5,8],[0,4,8],[2,4,6]];let options=["","","","","","","","",""],running=!1,currentPlayer="X",player=[{name:"X",score:0},{name:"Draw",score:0},{name:"O",score:0}];function gameInit(){cells.forEach(cell=>cell.addEventListener("click",cardClick,{once:!0})),restartBtn.addEventListener("click",restartGame),statusText.textContent=`${currentPlayer}'s turn`,running=!0,scoreUpdate()}function cardClick(){const clickCard=this.getAttribute("cellIndex");running&&updateCell(this,clickCard)}function updateCell(cell,index){options[index]=currentPlayer,cell.textContent=currentPlayer,checkWinner(currentPlayer)?(winnerText.textContent=`${currentPlayer} Win`,scoreUpdate(winner=currentPlayer),running=!1):isDraw()?(winnerText.textContent="Draw",scoreUpdate(winner="Draw"),running=!1):changePlayer()}function changePlayer(){currentPlayer="X"===currentPlayer?"O":"X",statusText.textContent=`${currentPlayer}'s turn`}function checkWinner(currentPlayer){return winConditions.some(combination=>combination.every(index=>options[index]===currentPlayer))}function isDraw(){return options.every(option=>""!==option)}function restartGame(){options.forEach((_,index)=>options[index]=""),cells.forEach(cell=>cell.textContent=""),currentPlayer="X",statusText.textContent="",winnerText.textContent="",gameInit()}function scoreUpdate(winner){const playerName=player.find(person=>person.name===winner);if(!playerName)return"No Player";playerName.score++,score=playerName.score,document.getElementById(playerName.name).textContent=score}gameInit();